import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { useTheme } from '../../contexts/ThemeContext';

// Register GSAP plugins
gsap.registerPlugin(ScrollTrigger);

const Navbar = ({ onLogout }) => {
  const { isDarkMode, toggleTheme } = useTheme();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');
  const [username, setUsername] = useState('');
  const navbarRef = useRef(null);
  const logoRef = useRef(null);
  const navLinksRef = useRef([]);
  const themeToggleRef = useRef(null);

  // Get username from localStorage
  useEffect(() => {
    const storedUsername = localStorage.getItem('userEmail');
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  // Theme toggle handler is now from context

  // Initial GSAP animations (only run once)
  useEffect(() => {
    const navbar = navbarRef.current;
    const logo = logoRef.current;
    const navLinks = navLinksRef.current;
    const themeToggle = themeToggleRef.current;

    // Initial animation timeline
    const tl = gsap.timeline();

    // Animate navbar entrance
    tl.fromTo(navbar, 
      { y: -100, opacity: 0 },
      { y: 0, opacity: 1, duration: 1, ease: "power3.out" }
    )
    // Animate logo
    .fromTo(logo,
      { scale: 0, rotation: -180 },
      { scale: 1, rotation: 0, duration: 0.8, ease: "back.out(1.7)" },
      "-=0.5"
    )
    // Animate navigation links
    .fromTo(navLinks,
      { y: 50, opacity: 0 },
      { y: 0, opacity: 1, duration: 0.6, stagger: 0.1, ease: "power2.out" },
      "-=0.3"
    )
    // Animate theme toggle
    .fromTo(themeToggle,
      { scale: 0, rotation: 180 },
      { scale: 1, rotation: 0, duration: 0.5, ease: "back.out(1.7)" },
      "-=0.2"
    );
  }, []); // Empty dependency array - only run once

  // Scroll-triggered animations (separate effect)
  useEffect(() => {
    const navbar = navbarRef.current;

    // Scroll-triggered animation
    ScrollTrigger.create({
      trigger: navbar,
      start: "top top",
      end: "bottom top",
      onEnter: () => {
        gsap.to(navbar, { 
          backgroundColor: isDarkMode ? "rgba(26, 32, 44, 0.95)" : "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(25px)",
          WebkitBackdropFilter: "blur(25px)",
          duration: 0.3
        });
      },
      onLeaveBack: () => {
        gsap.to(navbar, { 
          backgroundColor: isDarkMode ? "rgba(26, 32, 44, 0.8)" : "rgba(255, 255, 255, 0.8)",
          backdropFilter: "blur(20px)",
          WebkitBackdropFilter: "blur(20px)",
          duration: 0.3
        });
      }
    });

    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, [isDarkMode]); // Only re-run when isDarkMode changes

  // Scroll spy to detect active section
  useEffect(() => {
    const sections = ['hero', 'about', 'expertise', 'products', 'certifications',  'contact'];

    const handleScroll = () => {
      const scrollPosition = window.scrollY + 100; // Offset for navbar height

      for (let i = sections.length - 1; i >= 0; i--) {
        const section = document.getElementById(sections[i]);
        if (section) {
          const sectionTop = section.offsetTop;
          const sectionHeight = section.offsetHeight;

          if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            setActiveSection(sections[i]);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Check initial position

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Hover animations for nav links
  const handleLinkHover = (index, isHovering) => {
    const link = navLinksRef.current[index];
    if (link) {
      gsap.to(link, {
        scale: isHovering ? 1.1 : 1,
        color: isHovering ? (isDarkMode ? "#60A5FA" : "#3B82F6") : (isDarkMode ? "#E5E7EB" : "#374151"),
        duration: 0.3,
        ease: "power2.out"
      });
    }
  };

  // Theme toggle animation
  const handleThemeToggle = () => {
    const toggle = themeToggleRef.current;
    gsap.to(toggle, {
      rotation: "+=180",
      scale: 0.8,
      duration: 0.2,
      yoyo: true,
      repeat: 1,
      ease: "power2.inOut",
      onComplete: () => {
        toggleTheme();
      }
    });
  };

  return (

      {/* Bottom border line */}







          {/* Logo Section */}

 {
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
          >


               {
                  // Fallback to text logo if image fails to load
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              />
              {/* Fallback text logo */}


                AJ



              SUPPLIMENTS



          {/* Navigation Links */}


            {[
              { name: 'ABOUT', id: 'about', type: 'scroll' },
              { name: 'EXPERTISE', id: 'expertise', type: 'scroll' },
              { name: 'PRODUCTS', id: 'products', type: 'scroll' },
              // { name: 'CERTIFICATIONS', id: 'certifications', type: 'scroll' },
              { name: 'SOFTWARE', id: 'oursoftware', type: 'route' },
              { name: 'CONTACT', id: 'contact', type: 'scroll' }
            ].map((link, index) => {
              const isActive = link.type === 'scroll' ? activeSection === link.id : window.location.pathname === `/${link.id}`;

              if (link.type === 'route') {
                // Special handling for software route - check authentication
                const handleSoftwareClick = (e) => {
                  e.preventDefault();
                  const isLoggedIn = localStorage.getItem('isLoggedIn');
                  if (isLoggedIn === 'true') {
                    // If logged in, go to species selection
                    window.location.href = '/species';
                  } else {
                    // If not logged in, go to login
                    window.location.href = '/login';
                  }
                };

                return (
                   navLinksRef.current[index] = el}
                    href="#"
                    onClick={handleSoftwareClick}
                    style={{
                      fontWeight: 'bold',
                      fontSize: '14px',
                      letterSpacing: '0.2em',
                      padding: '8px 16px',
                      textDecoration: 'none',
                      transition: 'all 0.3s ease',
                      cursor: 'pointer',
                      color: isActive 
                        ? (isDarkMode ? '#60A5FA' : '#3B82F6')
                        : (isDarkMode ? '#E5E7EB' : '#374151'),
                      position: 'relative'
                    }}
                    className={`transition-all duration-300 ${
                      isDarkMode ? 'hover:text-blue-400' : 'hover:text-blue-600'
                    }`}
                    onMouseEnter={() => handleLinkHover(index, true)}
                    onMouseLeave={() => handleLinkHover(index, false)}
                  >
                    {link.name}
                    {/* Active indicator */}
                    {isActive && (


                    )}

                );
              }

              return (
                 navLinksRef.current[index] = el}
                  href={`#${link.id}`}
                  style={{
                    fontWeight: 'bold',
                    fontSize: '14px',
                    letterSpacing: '0.2em',
                    padding: '8px 16px',
                    textDecoration: 'none',
                    transition: 'all 0.3s ease',
                    cursor: 'pointer',
                    color: isActive 
                      ? (isDarkMode ? '#60A5FA' : '#3B82F6')
                      : (isDarkMode ? '#E5E7EB' : '#374151'),
                    position: 'relative'
                  }}
                  className={`transition-all duration-300 ${
                    isDarkMode ? 'hover:text-blue-400' : 'hover:text-blue-600'
                  }`}
                  onMouseEnter={() => handleLinkHover(index, true)}
                  onMouseLeave={() => handleLinkHover(index, false)}
                  onClick={(e) => {
                    e.preventDefault();
                    // Smooth scroll to section
                    const element = document.getElementById(link.id);
                    if (element) {
                      element.scrollIntoView({ behavior: 'smooth' });
                    }
                  }}
                >
                  {link.name}
                  {/* Active indicator */}
                  {isActive && (


                  )}

              );
            })}


          {/* User Info and Controls */}


            {/* Username Display */}
            {/* {username && (


                Welcome, {username}

            )} */}

            {/* Theme Toggle */}
             {
                e.target.style.transform = 'scale(1.1)';
                e.target.style.backgroundColor = isDarkMode ? '#4B5563' : '#D1D5DB';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'scale(1)';
                e.target.style.backgroundColor = isDarkMode ? '#374151' : '#E5E7EB';
              }}
              aria-label="Toggle theme"
            >
              {isDarkMode ? (



              ) : (



              )}


            {/* Logout Button */}
            {username && (

                Logout

            )}


          {/* Mobile Menu Button */}
           setIsMenuOpen(!isMenuOpen)}
          >






        {/* Mobile Menu */}
        {isMenuOpen && (




              {[
                { name: 'ABOUT', id: 'about', type: 'scroll' },
                { name: 'EXPERTISE', id: 'expertise', type: 'scroll' },
                { name: 'PRODUCTS', id: 'products', type: 'scroll' },
                { name: 'CERTIFICATIONS', id: 'certifications', type: 'scroll' },
                { name: 'SOFTWARE', id: 'oursoftware', type: 'route' }, 
                { name: 'CONTACT', id: 'contact', type: 'scroll' }
              ].map((link) => {
                const isActive = link.type === 'scroll' ? activeSection === link.id : window.location.pathname === `/${link.id}`;

                if (link.type === 'route') {
                  // Special handling for software route - check authentication
                  const handleMobileSoftwareClick = (e) => {
                    e.preventDefault();
                    setIsMenuOpen(false);
                    const isLoggedIn = localStorage.getItem('isLoggedIn');
                    if (isLoggedIn === 'true') {
                      // If logged in, go to species selection
                      window.location.href = '/species';
                    } else {
                      // If not logged in, go to login
                      window.location.href = '/login';
                    }
                  };

                  return (

                      {link.name}
                      {/* Active indicator for mobile */}
                      {isActive && (


                      )}

                  );
                }

                return (
                   setIsMenuOpen(false)}
                    style={{
                      color: isActive 
                        ? (isDarkMode ? '#60A5FA' : '#3B82F6')
                        : undefined
                    }}
                  >
                    {link.name}
                    {/* Active indicator for mobile */}
                    {isActive && (


                    )}

                );
              })}

              {/* Mobile Logout Button */}


                 {
                    onLogout();
                    setIsMenuOpen(false);
                  }}
                  className={`w-full py-3 px-4 rounded-lg font-medium text-sm transition-all duration-300 ${
                    isDarkMode 
                      ? 'bg-red-600 hover:bg-red-700 text-white' 
                      : 'bg-red-500 hover:bg-red-600 text-white'
                  }`}
                >
                  Logout




        )}


  );
};

export default Navbar;

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ProtectedRoute = ({ children }) => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAuth = () => {
      const isLoggedIn = localStorage.getItem('isLoggedIn');
      if (isLoggedIn !== 'true') {
        navigate('/login');
      } else {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, [navigate]);

  // Show loading while checking authentication
  if (isLoading) {
    return (







Loading...


    );
  }

  return children;
};

export default ProtectedRoute;
